namespace OmniIconDeps\Symfony\Bundle\FrameworkBundle\Command;

use OmniIconDeps\Symfony\Component\Cache\PruneableInterface;
use OmniIconDeps\Symfony\Component\Console\Attribute\AsCommand;
use OmniIconDeps\Symfony\Component\Console\Command\Command;
use OmniIconDeps\Symfony\Component\Console\Input\InputInterface;
use OmniIconDeps\Symfony\Component\Console\Output\OutputInterface;
use OmniIconDeps\Symfony\Component\Console\Style\SymfonyStyle;
/**
 * Cache pool pruner command.
 *
 * @author Rob Frawley 2nd <rmf@src.run>
 */
#[AsCommand(name: 'cache:pool:prune', description: 'Prune cache pools')]
final class CachePoolPruneCommand extends Command
{
    /**
     * @param iterable<mixed, PruneableInterface> $pools
     */
    public function __construct(private iterable $pools)
    {
        parent::__construct();
    }
    protected function configure(): void
    {
        $this->setHelp(<<<'EOF'
The <info>%command.name%</info> command deletes all expired items from all pruneable pools.

    %command.full_name%
EOF
);
    }
    protected function execute(InputInterface $input, OutputInterface $output): int
    {
        $io = new SymfonyStyle($input, $output);
        $exitCode = Command::SUCCESS;
        foreach ($this->pools as $name => $pool) {
            $io->comment(\sprintf('Pruning cache pool: <info>%s</info>', $name));
            if (!$pool->prune()) {
                $io->error(\sprintf('Cache pool "%s" could not be pruned.', $name));
                $exitCode = Command::FAILURE;
            }
        }
        if (Command::SUCCESS === $exitCode) {
            $io->success('Successfully pruned cache pool(s).');
        }
        return $exitCode;
    }
}
